import{_ as a,o as s,c as n,V as t}from"./chunks/framework.7cbf217e.js";const e="/assets/image-20230415001616761.1a2d5421.png",o="/assets/image-20230415001716818.ebe20568.png",C=JSON.parse('{"title":"移动WEB","description":"","frontmatter":{},"headers":[],"relativePath":"front-end/CSS/移动WEB/01-移动WEB.md","filePath":"front-end/CSS/移动WEB/01-移动WEB.md","lastUpdated":1683117492000}'),p={name:"front-end/CSS/移动WEB/01-移动WEB.md"},l=t('<h1 id="移动web" tabindex="-1">移动WEB <a class="header-anchor" href="#移动web" aria-label="Permalink to &quot;移动WEB&quot;">​</a></h1><h2 id="分辨率" tabindex="-1">分辨率 <a class="header-anchor" href="#分辨率" aria-label="Permalink to &quot;分辨率&quot;">​</a></h2><p><strong>屏幕尺寸</strong></p><ul><li>指的是屏幕对角线的长度，一般用英寸来度量</li></ul><p><strong>分辨率分类</strong></p><ul><li><p><strong>物理分辨率</strong>是生产屏幕时就固定的，它是不可被改变的</p></li><li><p><strong>逻辑分辨率</strong>是由软件（驱动）决定的</p></li></ul><p><strong>制作网页参考物理分辨率还是逻辑分辨率？</strong></p><ul><li><strong>逻辑分辨率</strong></li></ul><p><strong>主流设备分辨率</strong></p><p>一般使用iPhone6/7/8来设置</p><p><img src="'+e+`" alt="image-20230415001616761"></p><h2 id="视口" tabindex="-1">视口 <a class="header-anchor" href="#视口" aria-label="Permalink to &quot;视口&quot;">​</a></h2><p><strong>使用meta标签设置视口宽度，制作适配不同设备宽度的网页</strong></p><p>目标：网页宽度和设备宽度(分辨率)相同。</p><p>解决办法：添加视口标签。</p><p>视口：显示HTML网页的区域，用来约束HTML尺寸。</p><p><em>手机屏幕尺寸都不同，网页宽度为100%</em></p><p><em>网页的宽度和逻辑分辨率尺寸相同</em></p><p><strong>思考：默认情况下，网页的宽度和逻辑分辨率相同吗？</strong></p><ul><li>不同， 默认网页宽度是980px</li></ul><div class="language-css"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">&lt;</span><span style="color:#FFCB6B;">meta</span><span style="color:#A6ACCD;"> name=&quot;viewport&quot; content=&quot;width=</span><span style="color:#FFCB6B;">device-width</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">initial-scale</span><span style="color:#A6ACCD;">=1.0&quot;</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">1</span><span style="color:#89DDFF;">.</span><span style="color:#FFCB6B;">viewport：视口</span></span>
<span class="line"><span style="color:#A6ACCD;">2.width=</span><span style="color:#FFCB6B;">device-width</span><span style="color:#A6ACCD;">：视口宽度 = 设备宽度</span></span>
<span class="line"><span style="color:#A6ACCD;">3.</span><span style="color:#FFCB6B;">initial-scale</span><span style="color:#A6ACCD;">=1</span><span style="color:#89DDFF;">.</span><span style="color:#FFCB6B;">0：缩放1倍（不缩放）</span></span></code></pre></div><h3 id="二倍图" tabindex="-1">二倍图 <a class="header-anchor" href="#二倍图" aria-label="Permalink to &quot;二倍图&quot;">​</a></h3><p><strong>能够使用像素大厨软件测量二倍图中元素的尺寸</strong></p><p>图片分辨率, 为了高分辨率下图片不会模糊失真</p><p>二倍图一般指750像素宽度的图片</p><h3 id="设计稿尺寸" tabindex="-1">设计稿尺寸 <a class="header-anchor" href="#设计稿尺寸" aria-label="Permalink to &quot;设计稿尺寸&quot;">​</a></h3><p>现阶段设计稿参考iPhone6/7/8，设备宽度375px产出设计稿。</p><p>二倍图设计稿尺寸：750px</p><p><img src="`+o+'" alt="image-20230415001716818"></p><h3 id="百分比布局" tabindex="-1">百分比布局 <a class="header-anchor" href="#百分比布局" aria-label="Permalink to &quot;百分比布局&quot;">​</a></h3><p>能够使用百分比布局开发网页</p><ul><li><p>百分比布局， 也叫流式布局</p></li><li><p>效果： 宽度自适应，高度固定</p></li></ul>',32),r=[l];function i(c,d,h,_,g,u){return s(),n("div",null,r)}const B=a(p,[["render",i]]);export{C as __pageData,B as default};
